#!/bin/sh
### BEGIN INIT INFO
# Provides:          Mist
# Required-Start:    $local_fs $network $named $time $syslog
# Required-Stop:     $local_fs $network $named $time $syslog
# Default-Start:     2 3 4 5
# Default-Stop:      0 1 6
# Description:       Hydrosphere Mist
### END INIT INFO

export SPARK_HOME={{spark_home}}

set -e

start() {
    cd {{mist_dir}}
    /sbin/runuser {{mist_user}} -s /bin/bash -c "{{mist_dir}}/bin/mist-master start --config {{default_config_file}} --router-config {{default_routers_file}} --pid {{mist_pid_file}} --java-args {{mist_java_args}}"
}

stop() {
    {{mist_dir}}/bin/mist-master stop --pid {{mist_pid_file}}
}

case "$1" in
start)
start
;;
stop)
stop
;;
restart)
stop
start
;;
*)
echo "Usage: $0 {start|stop|restart}"
esac
